import requests
import os
from dotenv import load_dotenv

load_dotenv()

def update_order_status():
    mall_id = os.getenv("CAFE24_MALL_ID")
    access_token = os.getenv("CAFE24_ACCESS_TOKEN")

    url = f"https://{mall_id}.cafe24api.com/api/v2/admin/orders/{order_id}/status"

    headers = {
        "Authorization": f"Bearer {access_token}",
        "Content-Type": "application/json",
        "X-Cafe24-Api-Version": "2024-06-01"
    }

    data = {
        "status": "cancelled"  # 상태 코드 (pending, shipped, completed, cancelled)
    }

    try:
        response = requests.patch(url, headers=headers, json=data)
        response.raise_for_status()
        print(f"API 호출 성공 - 상태 코드: {response.status_code}")
        return response.json()

    except requests.HTTPError as e:
        print(f"API 오류: {e.response.status_code} - {e.response.text}")
        raise

